import React, { useState } from 'react';
import ImageUpload from './components/ImageUpload';
import MatrixInput from './components/MatrixInput';
import Visualizer from './components/Visualizer';
import solveZipPuzzle from './solver';

function App() {
  const [matrices, setMatrices] = useState({
    numbers: [],
    hWalls: [],
    vWalls: [],
    m: 0,
    n: 0,
  });
  const [path, setPath] = useState([]);
  const [solving, setSolving] = useState(false);
  const [error, setError] = useState('');

  const handleMatrices = (numbers, hWalls, vWalls) => {
    setMatrices({
      numbers,
      hWalls,
      vWalls,
      m: numbers.length,
      n: numbers[0].length,
    });
    setPath([]);
    setError('');
  };

  const handleSolve = () => {
    setSolving(true);
    setTimeout(() => {
      try {
        const result = solveZipPuzzle(
          matrices.numbers,
          matrices.hWalls,
          matrices.vWalls
        );
        if (result && result.length > 0) {
          setPath(result);
          setError('');
        } else {
          setPath([]);
          setError('No solution found.');
        }
      } catch (err) {
        setPath([]);
        setError('Error: ' + err.message);
      }
      setSolving(false);
    }, 100); // Allow UI to update
  };

  return (
    <div className="app-container">
      <h1>Linkedin Zip Solver</h1>
      <ImageUpload onExtracted={handleMatrices} />
      <MatrixInput
        numbers={matrices.numbers}
        hWalls={matrices.hWalls}
        vWalls={matrices.vWalls}
        setMatrices={handleMatrices}
      />
      <button
        style={{ margin: '20px 0', fontSize: '1.1rem', padding: '12px 24px' }}
        onClick={handleSolve}
        disabled={solving || matrices.m === 0}
      >
        {solving ? 'Solving...' : 'Solve Puzzle'}
      </button>
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}
      {path.length > 0 && (
        <Visualizer
          numbers={matrices.numbers}
          path={path}
          hWalls={matrices.hWalls}
          vWalls={matrices.vWalls}
        />
      )}
      <div style={{ marginTop: 32, color: '#888', fontSize: '0.95rem' }}>
        <b>How to use:</b> Upload a screenshot or enter the grid manually.  
        Adjust (if needed), then click <b>Solve Puzzle</b>.  
        The colored band shows the path. You can edit matrices if OCR is imperfect.
      </div>
    </div>
  );
}

export default App;
